export class SuperTabIndicatorComponent {
    constructor() {
        this.toolbarPosition = 'top';
    }
    hostData() {
        const positionStyle = {};
        if (this.toolbarPosition === 'bottom') {
            positionStyle.top = 0;
        }
        else {
            positionStyle.bottom = 0;
        }
        return {
            style: Object.assign({}, positionStyle),
        };
    }
    static get is() { return "super-tab-indicator"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "toolbarPosition": {
            "type": String,
            "attr": "toolbar-position"
        }
    }; }
    static get style() { return "/**style-placeholder:super-tab-indicator:**/"; }
}
